script:
frappe.ui.form.on('Leave Policy Assignment', {
	refresh(frm) {
		frm.set_query("employee", function() {
			return {
				filters: {
					'status': 'Active'
				}
			};
		});
	}
})

frappe.ui.form.on('Leave Application', {
	validate: function(frm) {
	    if(frm.doc.leave_type == '개인연차시간' ){
	        if(frm.doc.from_date != frm.doc.to_date){
    	        msgprint('개인연차시간은 시작일과 종료일이 같아야 합니다.');
                validated = false;
	        }
	        if(frm.doc.half_day == 1){
    	        msgprint('반차는 개인연차로 선택해주셔야 합니다.');
                validated = false;
	        }
	    }
		// your code here
	},
	hours:function(frm){
	    cur_frm.set_value('total_leave_days',frm.doc.hours*1);
	},
	from_date:function(frm){
	    cur_frm.set_value('posting_date',frm.doc.from_date);
	}
})
